import { jwtDecode } from "jwt-decode";
import { setisAdmin } from "../store/adminSlice";
const API_URL = "https://localhost:7014/api/Auth";

export const login = async (username, password, dispatch) => {
  const response = await fetch(`${API_URL}/login`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ username, password })
  });

  const data = await response.json();

  if (!response.ok || !data.result) {
    throw new Error(data.message || 'Login failed');
  }

  if (data.value) {
    const decodedToken = jwtDecode(data.value);
    const expirationDate = new Date(decodedToken.exp * 1000);
    const ROLE_KEY = "http://schemas.microsoft.com/ws/2008/06/identity/claims/role";
    const role = decodedToken[ROLE_KEY];
    if(role == "Admin"){
      dispatch(setisAdmin(true));
    }
    localStorage.setItem('token', data.value);
    localStorage.setItem('tokenExpiration', expirationDate);
  }

  return data;
};


export const signup = async (username, password, email) => {
  const response = await fetch(`${API_URL}/signup`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ username, password, email }),
  });

  const data = await response.json();

  if (!response.ok || !data.result) {
    throw new Error(data.message || 'Signup failed');
  }

  return data.value;
};

export const logout = (dispatch) => {
  dispatch(setisAdmin(false));
  localStorage.removeItem('token');
  localStorage.removeItem('tokenExpiration');
};

export const getToken = () => {
  const token = localStorage.getItem('token');
  const expiration = localStorage.getItem('tokenExpiration');

  if (token && expiration) {
    const now = new Date().getTime();
    if (now > expiration) {
      logout(); 
      return null;
    }
  }

  return token;
};
