import { deleteFeature } from "../store/wktSlice";
import {setHasDeleted} from "../store/featureSlice"
import { useSelector, useDispatch } from 'react-redux';

export const useDeleteItem = () => {
  const featureId = useSelector((state) => state.feature.id);
  const dispatch = useDispatch();

  const deleteItem = async () => {
    try {
      const response = await fetch(`https://localhost:7014/api/Logs/${featureId}`, {
        method: 'DELETE',
      });
      if (!response.ok) {
        throw new Error('Failed to delete item');
      }
      dispatch(deleteFeature(featureId));
      return true;
    } catch (error) {
      console.error(error.message);
      return false; 
    }
  };

  return deleteItem;
};
