import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  isAdmin: false,
};

const adminSlice = createSlice({
  name: 'admin',
  initialState,
  reducers: {
    setisAdmin: (state, action) => {
      state.isAdmin = action.payload;
    },
  },
});

export const {setisAdmin } = adminSlice.actions;
export default adminSlice.reducer;
