import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  id: null,
  log: "",
  name: "",
  zoom: "",
  hasDeleted: false,
};

const featureSlice = createSlice({
  name: 'feature',
  initialState,
  reducers: {
    setId: (state, action) => {
      state.id = action.payload;
    },
    setLog: (state, action) => {
      state.log = action.payload;
    },
    setName: (state, action) => {
        state.name = action.payload;
    },
    setZoom: (state, action) => {
        state.zoom = action.payload;
    },
    resetHasDeleted: (state) => {
        state.hasDeleted = false;
    }
  },
});

export const { setId, setLog, setName, setZoom, hasDeleted } = featureSlice.actions;
export const selectFeatureId = (state) => state.feature.id;
export const selectFeatureLog = (state) => state.feature.log;
export const selectFeatureName = (state) => state.feature.name;
export const selectFeatureZoom = (state) => state.feature.zoom;
export const selectHasDeleted = (state) => state.feature.hasDeleted;
export default featureSlice.reducer;
