import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { updateFeature } from './store/featureSlice';

export const EditFeature = ({ id, name, wkt, onClose }) => {
  const [localName, setLocalName] = useState(name);
  const [localWkt, setLocalWkt] = useState(wkt);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const dispatch = useDispatch();

  const handleSave = async () => {
    try {
      setLoading(true);
      setError(null);

      // Backend API isteği
      const response = await fetch(`/api/Logs/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: localName,
          log: localWkt,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to update feature');
      }

      const updatedFeature = await response.json();

      // Redux store'da güncelleme
      dispatch(updateFeature(updatedFeature));

      alert('Feature updated successfully!');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Modal>
        <div className="p-4 bg-gray-100 rounded shadow">
      {error && <div className="text-red-500 mb-4">{error}</div>}
      <div className="mb-4">
        <label className="block font-medium text-gray-700 mb-2">Name</label>
        <input
          type="text"
          value={localName}
          onChange={(e) => setLocalName(e.target.value)}
          className="w-full border border-gray-300 rounded p-2"
        />
      </div>
      <div className="mb-4">
        <label className="block font-medium text-gray-700 mb-2">WKT</label>
        <textarea
          value={localWkt}
          onChange={(e) => setLocalWkt(e.target.value)}
          className="w-full border border-gray-300 rounded p-2"
        />
      </div>
      <button
        onClick={handleSave}
        disabled={loading}
        className={`px-4 py-2 ${loading ? 'bg-gray-400' : 'bg-blue-500'} text-white rounded hover:bg-blue-600`}
      >
        {loading ? 'Saving...' : 'Save'}
      </button>
    </div>
    </Modal>
  );
};

export default EditFeature;
