import React from 'react';
import { useDispatch } from 'react-redux';
import { setDrawType, setDrawActive } from '../store/drawSlice';

const AddFeaturePopup = ({ onClose }) => {
  const dispatch = useDispatch();

  const handleDrawTypeSelect = (type) => {
    dispatch(setDrawType(type));
    dispatch(setDrawActive(true));
    onClose(); // Popup'u kapat
  };

  return (
    <div className="popup bg-white p-4 rounded shadow-md fixed top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
      <h3 className="text-lg font-semibold mb-4">Choose a Draw Type</h3>
      <button
        className="block w-full mb-2 p-2 bg-blue-500 text-white rounded hover:bg-blue-600"
        onClick={() => handleDrawTypeSelect('Point')}
      >
        Point
      </button>
      <button
        className="block w-full mb-2 p-2 bg-green-500 text-white rounded hover:bg-green-600"
        onClick={() => handleDrawTypeSelect('LineString')}
      >
        Line
      </button>
      <button
        className="block w-full p-2 bg-red-500 text-white rounded hover:bg-red-600"
        onClick={() => handleDrawTypeSelect('Polygon')}
      >
        Polygon
      </button>
    </div>
  );
};

export default AddFeaturePopup;
