import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  id: null,
  log: "",
  name: "",
  zoom: false,
};

const featureSlice = createSlice({
  name: 'feature',
  initialState,
  reducers: {
    setId: (state, action) => {
      state.id = action.payload;
    },
    setLog: (state, action) => {
      state.log = action.payload;
    },
    setName: (state, action) => {
        state.name = action.payload;
    },
    setZoom: (state, action) => {
        state.zoom = action.payload;
    }
  },
});

export const { setId, setLog, setName, setZoom } = featureSlice.actions;
export const selectFeatureId = (state) => state.feature.id;
export const selectFeatureLog = (state) => state.feature.log;
export const selectFeatureName = (state) => state.feature.name;
export const selectFeaturZoom = (state) => state.feature.zoom;
export default featureSlice.reducer;
