import React, { useState } from "react";
import ReactDOM from "react-dom";
import { setSelectActive } from '../store/selectSlice';
import { useDispatch } from 'react-redux';

const AddFeature = ({ feature, onClose, onSave }) => {
  const dispatch = useDispatch();
  const [name, setName] = useState(feature?.get("name") || "");

  const handleSave = () => {
    if (name.trim()) {
      feature.set("name", name);
      onSave(feature);
    } else {
      alert("Name cannot be empty!");
    }
    dispatch(setSelectActive(true));
  };

  const handleClose = () => {
    dispatch(setSelectActive(true));
    onClose();
  };

  return ReactDOM.createPortal(
    <div className="fixed z-60 inset-0 overflow-y-auto">
      <div className="flex items-center justify-center min-h-screen px-4 pt-4 pb-20 text-center sm:block sm:p-0">
        <div className="fixed inset-0 transition-opacity">
          <div className="absolute inset-0 bg-gray-500 opacity-75"></div>
        </div>
        <span className="hidden sm:inline-block sm:align-middle sm:h-screen"></span>
        <div className="inline-block align-bottom bg-white rounded-lg px-6 pt-5 pb-4 text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full sm:p-6">
          <div className="sm:flex sm:items-start">
            <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left w-full">
              <h3 className="text-lg leading-6 font-medium text-gray-900">Feature Details</h3>
              <div className="mt-2 w-full">
                <input
                  type="text"
                  className="block w-full p-2 border border-gray-300 rounded bg-gray-100 text-gray-900"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  placeholder="Enter feature name"
                />
              </div>
            </div>
          </div>
          <div className="mt-5 sm:mt-4 sm:flex sm:flex-row-reverse w-full">
            <button
              onClick={handleSave}
              className="inline-flex justify-center w-full sm:w-auto rounded-md border border-transparent px-4 py-2 bg-blue-600 text-base font-medium text-white shadow-sm hover:bg-blue-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 sm:ml-3 sm:text-sm"
            >
              Save
            </button>
            <button
              onClick={handleClose}
              className="mt-3 inline-flex justify-center w-full sm:w-auto rounded-md border border-gray-300 px-4 py-2 bg-white text-base font-medium text-gray-700 shadow-sm hover:text-gray-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-300 sm:mt-0 sm:text-sm"
            >
              Close
            </button>
          </div>
        </div>
      </div>
    </div>,
    document.body
  );
};

export default AddFeature;
