import React, { useState } from "react";
import Modal from "./Modal";

const AddFeature = ({ feature, onClose, onSave }) => {
  const [name, setName] = useState(feature?.get('name') || '');

  const handleSave = () => {
    if (name.trim()) {
      feature.set('name', name); // Feature'ın adı ayarlanır
      onSave(feature); // Kaydetme işlevi çağrılır
    } else {
      alert("Name cannot be empty!");
    }
  };

  return (
    <Modal title="Feature Details" onClose={onClose}>
      <div className="mb-4">
        <label className="block mb-2 text-sm font-medium text-gray-300">
          Name
        </label>
        <input
          type="text"
          className="block w-full p-2 border border-gray-500 rounded bg-gray-900 text-white"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Enter feature name"
        />
      </div>
        <button
          className="p-2 bg-green-500 flex text-white rounded hover:bg-green-600"
          onClick={handleSave}
        >
          Save
        </button>
    </Modal>
  );
};

export default AddFeature;
