import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { updateFeature } from '../store/wktSlice';

export const EditFeature = ({ id, name, wkt, userId, onClose }) => {
  const [localName, setLocalName] = useState(name);
  const [localWkt, setLocalWkt] = useState(wkt);
  const [localUserId, setLocalUserId] = useState(userId);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const dispatch = useDispatch();
  const geometry = localWkt.split("(")[0].trim();

  const handleSave = async () => {
    try {
      setLoading(true);
      setError(null);
      const token = localStorage.getItem("token");
  
      const response = await fetch(`https://localhost:7014/api/Logs/${id}`, {
        method: 'PUT',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          id,
          name: localName,
          log: localWkt,
          UserId: localUserId,
        }),
      });
  
      if (!response.ok) {
        throw new Error('Failed to update feature');
      }
  
      const data = await response.json();
  
      if (!data.result) {
        throw new Error(data.message || 'Failed to update feature');
      }
  
      dispatch(updateFeature(data.value)); 
      onClose();
  
      alert('Feature updated successfully!');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };
  
  return (
    <div className="fixed z-60 inset-0 overflow-y-auto">
      <div className="flex items-center justify-center min-h-screen px-4 pt-4 pb-20 text-center sm:block sm:p-0">
        <div className="fixed inset-0 transition-opacity">
          <div className="absolute inset-0 bg-gray-500 opacity-75"></div>
        </div>
        <span className="hidden sm:inline-block sm:align-middle sm:h-screen"></span>
        <div className="inline-block align-bottom bg-white rounded-lg px-4 pt-5 pb-4 text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full sm:p-6">
          <div className="sm:flex sm:items-start">
            <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left">
              <h3 className="text-lg leading-6 font-medium text-gray-900">Edit Feature</h3>
              <div className="mt-2">
                <label className="block text-sm font-medium text-gray-700">Name</label>
                <input
                  type="text"
                  value={localName}
                  onChange={(e) => setLocalName(e.target.value)}
                  className="mt-1 px-3 py-2 block w-full shadow-sm border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                />
              </div>
              <div className="mt-2">
                <label className="block text-sm font-medium text-gray-700">User ID</label>
                <input
                  type="text"
                  value={localUserId}
                  onChange={(e) => setLocalUserId(e.target.value)}
                  className="mt-1 px-3 py-2 block w-full shadow-sm border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                />
              </div>
              <div className="mt-2">
                <p className="text-sm leading-5 text-gray-500"><strong>Type:</strong> {geometry}</p>
              </div>
            </div>
          </div>
          <div className="mt-5 sm:mt-4 sm:flex sm:flex-row-reverse">
            <button
              onClick={handleSave}
              disabled={loading}
              className="inline-flex justify-center w-full rounded-md border border-transparent px-4 py-2 bg-blue-600 text-base leading-6 font-medium text-white shadow-sm hover:bg-blue-500 focus:outline-none focus:shadow-outline-blue transition ease-in-out duration-150 sm:text-sm sm:leading-5"
            >
              {loading ? 'Saving...' : 'Save'}
            </button>
            <button
              onClick={onClose}
              className="mt-3 inline-flex justify-center w-full rounded-md border border-gray-300 px-4 py-2 bg-white text-base leading-6 font-medium text-gray-700 shadow-sm hover:text-gray-500 focus:outline-none focus:border-blue-300 focus:shadow-outline-blue transition ease-in-out duration-150 sm:text-sm sm:leading-5"
            >
              Cancel
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditFeature;
