import React from "react";
import ReactDOM from "react-dom";
import { useDispatch } from "react-redux";
import { setDrawType, setDrawActive } from "../store/drawSlice";
import { setSelectActive } from "../store/selectSlice";

const DrawPopup = ({ onClose }) => {
  const dispatch = useDispatch();

  const handleDrawTypeSelect = (type) => {
    dispatch(setDrawType(type));
    dispatch(setDrawActive(true));
    dispatch(setSelectActive(false));
    onClose();
  };

  return ReactDOM.createPortal(
    <div className="fixed z-60 inset-0 overflow-y-auto">
      <div className="flex items-center justify-center min-h-screen px-4 pt-4 pb-20 text-center sm:block sm:p-0">
        {/* Arkaplan (Blur + Opacity) */}
        <div className="fixed inset-0 transition-opacity">
          <div className="absolute inset-0 bg-gray-500 opacity-75"></div>
        </div>

        {/* Modal İçeriği */}
        <span className="hidden sm:inline-block sm:align-middle sm:h-screen"></span>

        <div className="inline-block align-bottom bg-white rounded-lg px-6 pt-5 pb-4 text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-md sm:w-full sm:p-6">
          <div className="sm:flex sm:items-start">
            {/* İkon */}
            
            <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left w-full">
              <h3 className="text-lg leading-6 font-medium text-gray-900">
                Choose a Draw Type
              </h3>
            </div>
          </div>
          <div className="mt-5 space-y-2">
            <button
              onClick={() => handleDrawTypeSelect("Point")}
              className="w-full px-4 py-2 bg-blue-600 text-white font-medium rounded-md shadow-sm hover:bg-blue-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              Point
            </button>
            <button
              onClick={() => handleDrawTypeSelect("LineString")}
              className="w-full px-4 py-2 bg-blue-600 text-white font-medium rounded-md shadow-sm hover:bg-blue-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              Line
            </button>
            <button
              onClick={() => handleDrawTypeSelect("Polygon")}
              className="w-full px-4 py-2 bg-blue-600 text-white font-medium rounded-md shadow-sm hover:bg-blue-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              Polygon
            </button>
            <button
              onClick={onClose}
              className="w-full px-4 py-2 bg-white text-gray-700 font-medium rounded-md border border-gray-300 shadow-sm hover:text-gray-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-300"
            >
              Close
            </button>
          </div>
        </div>
      </div>
    </div>,
    document.body
  );
};

export default DrawPopup;
