const DrawPopup = ({ onClose }) => {
  const dispatch = useDispatch();

  const handleDrawTypeSelect = (type) => {
    dispatch(setDrawType(type));
    dispatch(setDrawActive(true));
    dispatch(setSelectActive(false));
    onClose();
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
      <div className="p-4 bg-white shadow-lg rounded-md w-64">
        <h2 className="text-lg font-semibold mb-4">Choose a Draw Type</h2>
        <button
          className="block w-full mb-2 px-4 py-2 bg-blue-600 text-white text-base font-medium rounded-md shadow-sm hover:bg-blue-500 focus:outline-none focus:ring-2 focus:ring-blue-400 transition ease-in-out duration-150"
          onClick={() => handleDrawTypeSelect("Point")}
        >
          Point
        </button>
        <button
          className="block w-full mb-2 px-4 py-2 bg-blue-600 text-white text-base font-medium rounded-md shadow-sm hover:bg-blue-500 focus:outline-none focus:ring-2 focus:ring-blue-400 transition ease-in-out duration-150"
          onClick={() => handleDrawTypeSelect("LineString")}
        >
          Line
        </button>
        <button
          className="block w-full px-4 py-2 bg-blue-600 text-white text-base font-medium rounded-md shadow-sm hover:bg-blue-500 focus:outline-none focus:ring-2 focus:ring-blue-400 transition ease-in-out duration-150"
          onClick={() => handleDrawTypeSelect("Polygon")}
        >
          Polygon
        </button>
        <button
          className="mt-4 w-full px-4 py-2 bg-gray-400 text-white rounded-md shadow-sm hover:bg-gray-500"
          onClick={onClose}
        >
          Close
        </button>
      </div>
    </div>
  );
};

export default DrawPopup;
