import React, { useState } from "react";
import ReactDOM from "react-dom";
import { setSelectActive } from '../store/selectSlice';
import { useDispatch } from 'react-redux';

const AddFeature = ({ feature, onClose, onSave }) => {
  const dispatch = useDispatch();
  const [name, setName] = useState(feature?.get('name') || '');

  const handleSave = () => {
    if (name.trim()) {
      feature.set('name', name);
      onSave(feature);
    } else {
      alert("Name cannot be empty!");
    }
    dispatch(setSelectActive(true));
  };

  const handleClose = () => {
    dispatch(setSelectActive(true));
    onClose();
  };

  return ReactDOM.createPortal(
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="bg-gray-800 text-white p-6 rounded shadow-lg w-1/3">
        <h3 className="text-lg font-semibold mb-4">Feature Details</h3>
        <div className="mb-4">
          <label className="block mb-2 text-sm font-medium text-gray-300">
            Name
          </label>
          <input
            type="text"
            className="block w-full p-2 border border-gray-500 rounded bg-gray-900 text-white"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Enter feature name"
          />
        </div>
        <div className="flex justify-end gap-2">
          <button
            className="p-2 bg-green-500 text-white rounded hover:bg-green-600"
            onClick={handleSave}
          >
            Save
          </button>
          <button
            className="p-2 bg-red-500 text-white rounded hover:bg-red-600"
            onClick={handleClose}
          >
            Close
          </button>
        </div>
      </div>
    </div>,
    document.body
  );
};

export default AddFeature;
