import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  showEditModal: false,
};

const showEditSlice = createSlice({
  name: 'showEdit',
  initialState,
  reducers: {
    setshowEditModal: (state, action) => {
      state.showEditModal = action.payload;
    },
  },
});

export const { setshowEditModal} = showEditSlice.actions;
export const selectShowEditModal = (state) => state.showEdit.showEditModal; 


export default showEditSlice.reducer;
