import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  drawType: null, // 'Point', 'LineString', 'Polygon' gibi
  drawActive: false,
};

const drawSlice = createSlice({
  name: 'draw',
  initialState,
  reducers: {
    setDrawType: (state, action) => {
      state.drawType = action.payload;
    },
    setDrawActive: (state, action) => {
      state.drawActive = action.payload;
    },
  },
});

export const { setDrawType, setDrawActive } = drawSlice.actions;
export default drawSlice.reducer;
