import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  newFeature: null,
  newFeatureId: null,
  isEditingDone: false,
};

const newFeatureSlice = createSlice({
  name: 'newFeature',
  initialState,
  reducers: {
    setNewFeature: (state, action) => {
      state.newFeature = action.payload;
    },
    setNewFeatureId: (state, action) => {
      state.newFeatureId = action.payload;
    },
    setIsEditingDone: (state, action) => {
        state.isEditingDone = action.payload;
      },
  },
});

export const { setNewFeature, setNewFeatureId, setIsEditingDone} = newFeatureSlice.actions;
export const selectNewFeature = (state) => state.newFeature.newFeature;
export const selectNewFeatureId = (state) => state.newFeature.newFeatureId; 
export const selectIsEditingDone = (state) => state.newFeature.isEditingDone; 


export default newFeatureSlice.reducer;
