import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

export const fetchWktData = createAsyncThunk('wkt/fetchWktData', async () => {
  const response = await fetch('https://localhost:7014/api/Logs');
  const data = await response.json();
  return data;
});

const wktSlice = createSlice({
  name: 'wkt',
  initialState: {
    wktData: [], 
    status: 'idle', 
    error: null,
    hasFetched: false,
  },
  reducers: {
    resetHasFetched: (state) => {
      state.hasFetched = false;
    },
    addFeature: (state, action) => {
      state.wktData.push(action.payload);
    },
    deleteFeature: (state, action) => {
      const id = action.payload;
      state.features = state.features.filter(feature => feature.id !== id);
    },
    updateFeature: (state, action) => {
      const updatedFeature = action.payload;
      const index = state.wktData.findIndex((feature) => feature.id === updatedFeature.id);
      if (index !== -1) {
        state.wktData[index] = updatedFeature;
      }
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchWktData.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(fetchWktData.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.wktData = action.payload;
        state.hasFetched = true; // API'den gelen veriyi sakla
      })
      .addCase(fetchWktData.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      });
  },
});

export const selectWktData = (state) => state.wkt.wktData;
export const { resetHasFetched, addFeature, updateFeature, deleteFeature } = wktSlice.actions;
export const selectHasFetched = (state) => state.wkt.hasFetched; // Durumu seç
export const selectWktStatus = (state) => state.wkt.status; // Yüklenme durumunu seç
export const selectWktError = (state) => state.wkt.error; // Hata mesajını seç
export default wktSlice.reducer;
