import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { updateFeature } from '../store/wktSlice';
import Modal from "./Modal";

export const EditFeature = ({ id, name, wkt, onClose }) => {
  const [localName, setLocalName] = useState(name);
  const [localWkt, setLocalWkt] = useState(wkt);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const dispatch = useDispatch();

  const handleSave = async () => {
    try {
      setLoading(true);
      setError(null);

      // Backend API isteği
      const response = await fetch(`https://localhost:7014/api/Logs/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          id: id,
          name: localName,
          log: localWkt,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to update feature');
      }

      const updatedFeature = await response.json();

      dispatch(updateFeature(updatedFeature));
      onClose();

      alert('Feature updated successfully!');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Modal title="Edit Feature" onClose={onClose}>
        <div className="mb-4" >
      {error && <div className="text-red-500 mb-4">{error}</div>}
      <div className="mb-4">
        <label className="block mb-2 text-sm font-medium text-gray-300">Name</label>
        <input
          type="text"
          value={localName}
          onChange={(e) => setLocalName(e.target.value)}
          className="block w-full p-2 border border-gray-500 rounded bg-gray-900 text-white"
        />
      </div>
      <div className="mb-4">
        <label className="block mb-2 text-sm font-medium text-gray-300">WKT</label>
        <textarea
          value={localWkt}
          onChange={(e) => setLocalWkt(e.target.value)}
          className="block w-full p-2 border border-gray-500 rounded bg-gray-900 text-white"
        />
      </div>
      <button
        onClick={handleSave}
        disabled={loading}
        className={`px-4 py-2 ${loading ? 'bg-gray-400' : 'bg-blue-500'} text-white rounded hover:bg-blue-600`}
      >
        {loading ? 'Saving...' : 'Save'}
      </button>
    </div>
    </Modal>
  );
};

export default EditFeature;
