import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { setDeletedFeatureId } from "./featureSlice";

export const fetchUsers = createAsyncThunk("user/fetchUsers", async (_, { rejectWithValue }) => {
  const token = localStorage.getItem("token");
  try {
    const response = await fetch("https://localhost:7014/api/Users", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`,
      },
    });

    if (!response.ok) {
      throw new Error("Network response was not ok");
    }

    const data = await response.json();

    if (!data.result) {
      return rejectWithValue(data.message);
    }

    return data.value;
  } catch (error) {
    return rejectWithValue(error.message);
  }
});

const userSlice = createSlice({
  name: "user",
  initialState: {
    users: [],
    usersMap: new Map(),
    deletedUserId: null,
    status: "idle",
    error: null,
  },
  reducers: {
    addUser: (state, action) => {
      state.users.push(action.payload);
    },
    
    updateUser: (state, action) => {
      const updatedUser = action.payload;
      const index = state.users.findIndex((user) => user.id === updatedUser.id);
      if (index !== -1) {
        state.users[index] = updatedUser;
      }
    },
    updateUserMap: (state, action) => {
      const { id, name } = action.payload;
      state.usersMap.set(id, name);
    },
    deleteUserMap: (state, action) => {
      state.usersMap.delete(action.payload);
    },
    deleteUser: (state, action) => {
      const id = action.payload;
      state.users = state.users.filter((user) => user.id !== id);
    },
    clearUsers: (state) => {
      state.users = [];
    },
    setDeletedUserId: (state, action) => {
      state.deletedUserId = action.payload;
  },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchUsers.pending, (state) => {
        state.status = "loading";
      })
      .addCase(fetchUsers.fulfilled, (state, action) => {
        state.status = "succeeded";
        state.users = action.payload;
        state.usersMap = new Map(action.payload.map(user => [user.id, user.username]));
      })
      .addCase(fetchUsers.rejected, (state, action) => {
        state.status = "failed";
        state.error = action.payload;
      });
  },
});

export const selectUsers = (state) => state.user.users;
export const { addUser, updateUser, deleteUser, clearUsers, setDeletedUserId, setUsers, updateUserMap, deleteUserMap } = userSlice.actions;
export const selectUserStatus = (state) => state.user.status;
export const selectUserError = (state) => state.user.error;
export const selectDeletedUserId = (state) => state.user.deletedUserId;

export default userSlice.reducer;
