import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { selectUsers } from "../store/userSlice";
import { setDeletedUserId } from "../store/userSlice";
import ConfirmModal from "./ConfirmModal";
import EditUser from "./EditUser";
import { useDeleteUser} from '../utils/deleteUser';




const UserTable = ({ onClose }) => {
  const dispatch = useDispatch();
  const users = useSelector(selectUsers);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showUpdateModal, setShowUpdateModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const deleteUser = useDeleteUser();

  const handleDeleteClick = (user) => {
    setSelectedUser(user);
    dispatch(setDeletedUserId(user.id));
    setShowDeleteModal(true);
  };

  const handleUpdateClick = (user) => {
    setShowUpdateModal(true);
  };

  const handleDeleteConfirm = () => {
    deleteUser();
    setShowDeleteModal(false);
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-gray-500 bg-opacity-75 z-50">
      <div className="relative bg-white rounded-lg overflow-hidden shadow-xl sm:max-w-4xl w-full">
        <button
          className="absolute top-3 right-3 text-gray-500 hover:text-gray-700"
          onClick={onClose}
        >
          âœ•
        </button>
        <div className="p-6">
          <h3 className="text-lg font-medium text-gray-900 mb-4">User List</h3>
          <div className="max-h-96 overflow-y-auto border rounded">
            {users.length > 0 ? (
              <table className="table-auto w-full text-left">
                <thead className="bg-gray-200">
                  <tr>
                    <th className="px-4 py-2">ID</th>
                    <th className="px-4 py-2">Username</th>
                    <th className="px-4 py-2">Email</th>
                    <th className="px-4 py-2">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map((user) => (
                    <tr key={user.id} className="border-t text-gray-700">
                      <td className="px-4 py-2">{user.id}</td>
                      <td className="px-4 py-2">{user.username}</td>
                      <td className="px-4 py-2">{user.email}</td>
                      <td className="px-4 py-2 flex space-x-2">
                        <button
                          className="border px-4 py-2 bg-white text-gray-700 shadow-sm hover:text-gray-500"
                          onClick={() => handleUpdateClick(user)}
                        >
                          Update
                        </button>
                        <button
                          className="border px-4 py-2 bg-white text-gray-700 shadow-sm hover:text-gray-500"
                          onClick={() => handleDeleteClick(user)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p className="text-gray-500 text-center">No users available.</p>
            )}
          </div>
        </div>

        {showDeleteModal && (
          <ConfirmModal
            title={`Delete '${selectedUser.username}'?`}
            description="Are you sure you want to delete this user?"
            onCancel={() => setShowDeleteModal(false)}
            onConfirm={handleDeleteConfirm}
          />
        )}
        {showUpdateModal && (
          <EditUser
          />
        )}
      </div>
    </div>
  );
};

export default UserTable;
