import { deleteFeature } from "../store/wktSlice";
import { useSelector, useDispatch } from 'react-redux';


const featureId = useSelector((state) => state.feature.id);
const dispatch = useDispatch();

export const deleteItem = async () => {
    try {
      const response = await fetch(`https://localhost:7014/api/Logs/${id}`, {
        method: 'DELETE',
      });
      if (!response.ok) {
        throw new Error('Failed to delete item');
      }
      dispatch(deleteFeature(featureId));
      return true; // Silme işlemi başarılı
    } catch (error) {
      console.error(error.message);
      return false; // Silme işlemi başarısız
    }
  };
