import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  featureToModify: null,
  showUpdateModal: false,
  showUpdateFeature: false,
};

const updateSlice = createSlice({
  name: 'update',
  initialState,
  reducers: {
    setFeatureToModify: (state, action) => {
      state.featureToModify = action.payload;
    },
    setShowUpdateModal: (state, action) => {
      state.showUpdateModal = action.payload;
    },
    setShowUpdateFeature: (state, action) => {
      state.showUpdateFeature = action.payload;
    },
  },
});

export const { setFeatureToModify, setShowUpdateModal, setShowUpdateFeature} = updateSlice.actions;
export const selectFeatureToModify = (state) => state.update.featureToModify;
export const selectShowUpdateModal = (state) => state.update.showUpdateModal; 
export const selectShowUpdateFeature = (state) => state.update.showUpdateFeature; 

export default updateSlice.reducer;
