import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  selectActive: false,
};

const selectSlice = createSlice({
  name: 'selectFeature',
  initialState,
  reducers: {
    setSelectActive: (state, action) => {
      state.selectActive = action.payload;
    },
  },
});

export const { setSelectActive } = selectSlice.actions;
export const selectSelectActive = (state) => state.selectFeature.selectActive; // Durumu seç
export default selectSlice.reducer;
