import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  username: "",
  id: null,
  email: "",
};

const userToUpdateSlice = createSlice({
  name: 'userToUpdate',
  initialState,
  reducers: {
    setUser: (state, action) => {
      state.username = action.payload.username;
      state.id = action.payload.id;
      state.email = action.payload.email;
    },
    
  },
});

export const { setUser} = userToUpdateSlice.actions;
export const selectUsername = (state) => state.userToUpdate.username;
export const selectId = (state) => state.userToUpdate.id;
export const selectEmail = (state) => state.userToUpdate.email;




export default userToUpdateSlice.reducer;
