import React, { useEffect, useRef, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchWktData, addFeature } from '../store/wktSlice';
import { Map } from 'ol'; 
import { getCenter } from 'ol/extent'; 
import View from 'ol/View'; 
import { Tile as TileLayer } from 'ol/layer';
import { Vector as VectorLayer } from 'ol/layer';
import { Vector as VectorSource } from 'ol/source';
import OSM from 'ol/source/OSM';
import { WKT } from 'ol/format';
import { fromLonLat } from 'ol/proj';
import { Draw } from 'ol/interaction';
import { setDrawActive } from '../store/drawSlice';
import AddFeature from './AddFeature';
import { setZoom, setDeletedFeatureId} from '../store/featureSlice';
import { Select } from "ol/interaction";
import { click } from "ol/events/condition";
import { defaults as defaultControls } from 'ol/control';
import FeaturePopup from './ShowFeature';
import { setSelectActive } from '../store/selectSlice';
import Modify from "ol/interaction/Modify";
import Translate from "ol/interaction/Translate";
import Collection from "ol/Collection";
import StopEditingButton from './StopEditingButton';
import FeatureModal from './UpdateFeature';
import { setShowUpdateFeature } from '../store/updateSlice';
import { setIsEditingDone, setNewFeature } from '../store/newFeatureSlice';


const MapComponent = () => {
  const dispatch = useDispatch();
  const wktData = useSelector((state) => state.wkt.wktData);
  const status = useSelector((state) => state.wkt.status);
  const drawType = useSelector((state) => state.draw.drawType);
  const drawActive = useSelector((state) => state.draw.drawActive);
  const zoomMap = useSelector((state) => state.feature.zoom);
  const featureId = useSelector((state) => state.feature.id);
  const featureLog = useSelector((state) => state.feature.log);
  const featureName = useSelector((state) => state.feature.name);
  const deletedFeatureId = useSelector((state) => state.feature.deletedFeatureId);
  const selectAvailable = useSelector((state) => state.select.selectActive);
  const featureToModify = useSelector((state) => state.update.featureToModify);
  const showUpdateModal = useSelector((state) => state.update.showUpdateModal);
  const showUpdateFeature = useSelector((state) =>state.update.showUpdateFeature);
  const isEditingDone = useSelector((state) => state.newFeature.isEditingDone);
  const newFeatureId = useSelector((state) => state.newFeature.newFeatureId);
  const updateCanceled = useSelector((state) => state.update.updateCanceled);


  const mapRef = useRef();
  const selectInteraction = useRef(null);
  const vectorSource = useRef(new VectorSource());
  const modifyInteraction = useRef(null);
  const translateInteraction = useRef(null);
  const drawInteraction = useRef(null);
  const [selectedFeature, setSelectedFeature] = useState(null);
  const [clickedFeature, setClickedFeature] = useState(null);
  const [showClickedFeature, setShowClickedFeature] = useState(false);


  useEffect(() => {
    dispatch(fetchWktData());
  }, [dispatch]);

  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const token = localStorage.getItem("token"); 
        if (!token) {
          setError("Token bulunamadı. Lütfen giriş yapın.");
          return;
        }
  
        const response = await fetch("https://localhost:7014/api/Users", {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });
  
        if (!response.ok) {
          throw new Error("Kullanıcılar yüklenirken hata oluştu.");
        }
  
        const data = await response.json();
  
        if (!data.result) {
          throw new Error(data.message || "Kullanıcılar yüklenirken hata oluştu.");
        }
  
        setUsers(data.value); // Kullanıcı listesini `setUsers` ile güncelle
      } catch (err) {
        setError(err.message);
      }
    };
  
    fetchUsers();
  }, []);
  
  useEffect(() => {
    if (!mapRef.current) {
      mapRef.current = new Map({
        target: 'map',
        layers: [
          new TileLayer({ source: new OSM() }),
          new VectorLayer({ source: vectorSource.current }),
        ],
        controls: [],
        view: new View({ center: fromLonLat([29.035767, 41.065685]), zoom: 6 }),
      });

      selectInteraction.current = new Select({
        condition: click,
      });

      mapRef.current.addInteraction(selectInteraction.current);
      selectInteraction.current.on('select', (event) => {
        const selected = event.selected[0];
        setClickedFeature(selected || null);
        setShowClickedFeature(true);
      });
    }
  }, []);

  useEffect(() => {
    if (status === 'succeeded') {
      const format = new WKT();
      wktData.forEach(({ log, id, name }) => {
        const feature = format.readFeature(log, {
          dataProjection: 'EPSG:3857',
          featureProjection: 'EPSG:3857',
        });

        feature.setProperties({ id, name });
        vectorSource.current.addFeature(feature);
      });
    }
  }, [status]);

  useEffect(() => {
    if(isEditingDone) {
      const featureToChange = vectorSource.current.getFeatures().find(
        (feature) => feature.get('id') === newFeatureId
      );
      if(featureToChange) {
        dispatch(setNewFeature(featureToChange));
      }
      dispatch(setShowUpdateFeature(true));
      dispatch(setIsEditingDone(false));
    }
  }, [isEditingDone]);

  useEffect(() => {
    if(updateCanceled) {
      vectorSource.current.removeFeature(clickedFeature);
      const format = new WKT();
      const targetFeatureData = wktData.find(feature => feature.id === newFeatureId);
      const feature = format.readFeature(targetFeatureData.log, {
        dataProjection: 'EPSG:3857',
        featureProjection: 'EPSG:3857'
      });
      feature.setProperties({
        id: targetFeatureData.id,
        name: targetFeatureData.name
      });
      vectorSource.current.addFeature(feature);
    }
  }, [updateCanceled]);

  useEffect(() => {
    if (drawInteraction.current) {
      mapRef.current.removeInteraction(drawInteraction.current);
    }

    if (drawActive && drawType) {

      drawInteraction.current = new Draw({
        source: vectorSource.current,
        type: drawType,
      });

      drawInteraction.current.on('drawend', (event) => {
        const feature = event.feature;
        setSelectedFeature(feature);
        mapRef.current.removeInteraction(drawInteraction.current);
        dispatch(setDrawActive(false));
      });

      mapRef.current.addInteraction(drawInteraction.current);
    }
  }, [drawType, drawActive, dispatch]);

  useEffect(() => {
    if (selectInteraction.current) {
      selectInteraction.current.setActive(selectAvailable);
    }
  }, [selectAvailable]);

  useEffect(() => {
    if (modifyInteraction.current) {
      mapRef.current.removeInteraction(modifyInteraction.current);
      modifyInteraction.current = null;
    }
  
    if (translateInteraction.current) {
      mapRef.current.removeInteraction(translateInteraction.current);
      translateInteraction.current = null;
    }
  
    if (featureToModify) {
      modifyInteraction.current = new Modify({
        features: new Collection([featureToModify]),
      });
      mapRef.current.addInteraction(modifyInteraction.current);
  
      modifyInteraction.current.on("modifyend", () => {
        vectorSource.current.removeFeature(featureToModify);
        vectorSource.current.addFeature(featureToModify);
      });
  
      translateInteraction.current = new Translate({
        features: new Collection([featureToModify]),
      });
      mapRef.current.addInteraction(translateInteraction.current);
  
      translateInteraction.current.on("translateend", () => {
        vectorSource.current.removeFeature(featureToModify);
        vectorSource.current.addFeature(featureToModify);
      });
    }
  }, [featureToModify]);
  


  useEffect(() => {
    if(zoomMap !== "") {
      const wktFormat = new WKT();
      const feature = wktFormat.readFeature(zoomMap, {
      dataProjection: 'EPSG:3857',
      featureProjection: 'EPSG:3857',
    });
    console.log(featureLog);
    const featureCenter = getCenter(feature.getGeometry().getExtent());

    const view = mapRef.current.getView();
    view.setCenter(featureCenter);

    const targetZoom = 9;
    view.setZoom(targetZoom);
    dispatch(setZoom(""));
    }
  }, [zoomMap ,dispatch, featureLog]);

  useEffect(() => {
    if (deletedFeatureId) {
      const features =vectorSource.current.getFeatures();
      console.log(features);
      const featureToRemove = vectorSource.current.getFeatures().find(
        (feature) => feature.get('id') === deletedFeatureId
      );

      if (featureToRemove) {
        vectorSource.current.removeFeature(featureToRemove);
        dispatch(setDeletedFeatureId(null));
      }
    }
  }, [deletedFeatureId]);
  
  /*const handleSaveFeature = async (feature) => {
    const wkt = new WKT().writeFeature(feature);
    const name = feature.get('name');
    const newFeature = feature;

    try {
      const response = await fetch('https://localhost:7014/api/Logs', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name, log: wkt }),
      });

      if (response.ok) {
        const savedFeature = await response.json();
        newFeature.set('id', savedFeature.value.id);
        vectorSource.current.addFeature(newFeature);
        dispatch(setSelectActive(true));
        alert('Feature saved successfully!');
        dispatch(addFeature(savedFeature.value));
      } else {
        alert('Failed to save feature.');
      }
    } catch (error) {
      console.error('Error saving feature:', error);
    }
  }; */

  const handleSaveFeature = async (feature) => {
    // Feature'ı WKT formatına çevir
    const wkt = new WKT().writeFeature(feature);
    const name = feature.get('name');
    const newFeature = feature;

    try {
        // Local Storage'dan token'ı al
        const token = localStorage.getItem('token');
        if (!token) {
            alert('Authentication token not found!');
            return;
        }

        // Token'ı decode edip kullanıcı ID'sini al
        const tokenPayload = JSON.parse(atob(token.split('.')[1])); // JWT payload'ı al
        const userId = tokenPayload.sub; // Kullanıcı ID'si "sub" claim'inde olmalı

        // API isteğini yap
        const response = await fetch('https://localhost:7014/api/Logs', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`, // Token'ı Authorization başlığına ekle
            },
            body: JSON.stringify({ name, log: wkt, userId }), // userId'yi body'ye ekle
        });

        if (response.ok) {
            const savedFeature = await response.json();
            newFeature.set('id', savedFeature.value.id);
            vectorSource.current.addFeature(newFeature);
            dispatch(setSelectActive(true));
            alert('Feature saved successfully!');
            dispatch(addFeature(savedFeature.value));
        } else {
            alert('Failed to save feature.');
        }
    } catch (error) {
        console.error('Error saving feature:', error);
    }
};


  const removeFeature = (feature) => {
    vectorSource.current.removeFeature(selectedFeature);
    setSelectedFeature(null);
  }

  const onSave = () => {
    handleSaveFeature(selectedFeature);
    removeFeature(selectedFeature);
  };

  return (
    <div className="relative">
      <div id="map" className="w-full h-screen" ></div>
      {selectedFeature && (
        <AddFeature
          feature={selectedFeature}
          onClose={() => removeFeature(selectedFeature)}
          onSave={onSave}
        />
      )}
      {showClickedFeature && clickedFeature &&(
        <FeaturePopup
          feature={clickedFeature}
          onClose={() => setShowClickedFeature(false)}
        />
      )}
      {showUpdateModal && (
        <StopEditingButton
        />
      )}
      {showUpdateFeature && (
        <FeatureModal
        />
      )}

    </div>
  );
};

export default MapComponent;
