import React, { useEffect, useRef, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchWktData, addFeature } from '../store/wktSlice';
import { Map } from 'ol'; 
import { getCenter } from 'ol/extent'; 
import View from 'ol/View'; 
import { Tile as TileLayer } from 'ol/layer';
import { Vector as VectorLayer } from 'ol/layer';
import { Vector as VectorSource } from 'ol/source';
import OSM from 'ol/source/OSM';
import { WKT } from 'ol/format';
import { fromLonLat } from 'ol/proj';
import { Draw } from 'ol/interaction';
import { setDrawActive } from '../store/drawSlice';
import AddFeature from './AddFeature';
import { setZoom, setDeletedFeatureId} from '../store/featureSlice';
import { V } from 'ol/renderer/webgl/FlowLayer';

const MapComponent = () => {
  const dispatch = useDispatch();
  const wktData = useSelector((state) => state.wkt.wktData);
  const status = useSelector((state) => state.wkt.status);
  const drawType = useSelector((state) => state.draw.drawType);
  const drawActive = useSelector((state) => state.draw.drawActive);
  const zoomMap = useSelector((state) => state.feature.zoom);
  const featureId = useSelector((state) => state.feature.id);
  const featureLog = useSelector((state) => state.feature.log);
  const featureName = useSelector((state) => state.feature.name);
  const deletedFeatureId = useSelector((state) => state.feature.deletedFeatureId);
  

  const mapRef = useRef();
  const vectorSource = useRef(new VectorSource());
  const drawInteraction = useRef(null);
  const [selectedFeature, setSelectedFeature] = useState(null); // Çizim tamamlandığında özellik

  useEffect(() => {
    dispatch(fetchWktData()); // Sayfa yüklendiğinde thunk'ı tetikle
  }, [dispatch]);

  useEffect(() => {
    if (!mapRef.current) {
      mapRef.current = new Map({
        target: 'map',
        layers: [
          new TileLayer({ source: new OSM() }),
          new VectorLayer({ source: vectorSource.current }),
        ],
        view: new View({ center: fromLonLat([29.035767, 41.065685]), zoom: 6 }),
      });
    }
  }, []);

  useEffect(() => {
    if (status === 'succeeded') {
      const format = new WKT();
      wktData.forEach(({ log, id, name }) => {
        const feature = format.readFeature(log, {
          dataProjection: 'EPSG:3857',
          featureProjection: 'EPSG:3857',
        });

        feature.setProperties({ id, name });
        vectorSource.current.addFeature(feature);
      });
    }
  }, [wktData, status]);

  useEffect(() => {
    if (drawInteraction.current) {
      mapRef.current.removeInteraction(drawInteraction.current); // Önceki etkileşimi kaldır
    }

    if (drawActive && drawType) {
      drawInteraction.current = new Draw({
        source: vectorSource.current,
        type: drawType,
      });

      drawInteraction.current.on('drawend', (event) => {
        const feature = event.feature; 
        setSelectedFeature(feature);

        dispatch(setDrawActive(false));
      });

      mapRef.current.addInteraction(drawInteraction.current);
    }
  }, [drawType, drawActive, dispatch]);

  useEffect(() => {
    if(zoomMap != "") {
      const wktFormat = new WKT();
      const feature = wktFormat.readFeature(zoomMap, {
      dataProjection: 'EPSG:3857',
      featureProjection: 'EPSG:3857',
    });
    console.log(featureLog);
    const featureCenter = getCenter(feature.getGeometry().getExtent());

    const view = mapRef.current.getView();
    view.setCenter(featureCenter);

    const targetZoom = 9;
    view.setZoom(targetZoom);
    dispatch(setZoom(""));
    }
  }, [zoomMap ,dispatch, featureLog]);

  useEffect(() => {
    if (deletedFeatureId) {
      const featureToRemove = vectorSource.current.getFeatures().find(
        (feature) => feature.get('id') === deletedFeatureId
      );

      if (featureToRemove) {
        vectorSource.current.removeFeature(featureToRemove);
        dispatch(setDeletedFeatureId(null));
      }
    }
  }, [deletedFeatureId]);

  const handleSaveFeature = async (feature) => {
    const wkt = new WKT().writeFeature(feature); // WKT formatına çevir
    const name = feature.get('name');

    try {
      const response = await fetch('https://localhost:7014/api/Logs', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name, log: wkt }),
      });

      if (response.ok) {
        const savedFeature = await response.json();
      alert('Feature saved successfully!');
      if (selectedFeature) {
        selectedFeature.set('id', savedFeature.id);
      }
      dispatch(addFeature(savedFeature));
      setSelectedFeature(null);
      } else {
        alert('Failed to save feature.');
      }
    } catch (error) {
      console.error('Error saving feature:', error);
    }
  };

  return (
    <div className="relative">
      <div id="map" className="w-full h-screen" ></div>
      {selectedFeature && (
        <AddFeature
          feature={selectedFeature}
          onClose={() => setSelectedFeature(null)}
          onSave={handleSaveFeature}
        />
      )}
    </div>
  );
};

export default MapComponent;
