import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { updateUser } from '../store/userSlice';
import { selectUsername, selectEmail, selectId, selectRole } from '../store/userToUpdateSlice';

export const EditUser = ({ onClose }) => {
  const reduxUsername = useSelector(selectUsername);
  const [localUsername, setLocalUsername] = useState(reduxUsername);
  const reduxEmail = useSelector(selectEmail);
  const [localEmail, setLocalEmail] = useState(reduxEmail);
  const reduxRole = useSelector(selectRole);
  const [localRole, setLocalRole] = useState(reduxRole);
  const id = useSelector(selectId);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const dispatch = useDispatch();

  const handleSave = async () => {
    if (!localUsername.trim() || !localEmail.trim()) {
      setError("Username and Email cannot be empty.");
      return;
    }

    try {
      setLoading(true);
      setError(null);
      const token = localStorage.getItem("token");

      const response = await fetch(`https://localhost:7014/api/Users/${id}`, {
        method: 'PUT',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          id,
          username: localUsername,
          email: localEmail,
          role: localRole,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to update user');
      }

      const data = await response.json();

      if (!data.result) {
        throw new Error(data.message || 'Failed to update user');
      }

      dispatch(updateUser(data.value));
      alert('User updated successfully!');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed z-60 inset-0 overflow-y-auto">
      <div className="flex items-center justify-center min-h-screen px-4 pt-4 pb-20 text-center sm:block sm:p-0">
        <div className="fixed inset-0 transition-opacity">
          <div className="absolute inset-0 bg-gray-500 opacity-75"></div>
        </div>
        <span className="hidden sm:inline-block sm:align-middle sm:h-screen"></span>
        <div className="inline-block align-bottom bg-white rounded-lg px-4 pt-5 pb-4 text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full sm:p-6">
          <h3 className="text-lg leading-6 font-medium text-gray-900">Edit User</h3>
          <div className="mt-2">
            <label className="block text-sm font-medium text-gray-700">Username</label>
            <input type="text" value={localUsername} onChange={(e) => setLocalUsername(e.target.value)} className="mt-1 px-3 py-2 block w-full text-gray-700 shadow-sm border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 sm:text-sm" />
          </div>
          <div className="mt-2">
            <label className="block text-sm font-medium text-gray-700">Email</label>
            <input type="email" value={localEmail} onChange={(e) => setLocalEmail(e.target.value)} className="mt-1 px-3 py-2 block w-full shadow-sm text-gray-700 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 sm:text-sm" />
          </div>
          <div className="mt-2">
            <label className="block text-sm font-medium text-gray-700">Role</label>
            <select value={localRole} onChange={(e) => setLocalRole(e.target.value)} className="mt-1 px-3 py-2 block w-full text-gray-700 shadow-sm border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 sm:text-sm">
              <option value="User">User</option>
              <option value="Admin">Admin</option>
            </select>
          </div>
          {error && <p className="text-red-500 text-sm mt-2">{error}</p>}
          <div className="mt-5 sm:mt-4 sm:flex sm:justify-end sm:space-x-2">
            <button onClick={onClose} className="rounded-md border border-gray-300 px-4 py-2 bg-white text-gray-700 shadow-sm hover:text-gray-500 focus:outline-none focus:border-blue-300 focus:shadow-outline-blue transition ease-in-out duration-150 sm:text-sm">
              Cancel
            </button>
            <button onClick={handleSave} disabled={loading} className="rounded-md border border-transparent px-4 py-2 bg-blue-600 text-white shadow-sm hover:bg-blue-500 focus:outline-none focus:shadow-outline-blue transition ease-in-out duration-150 sm:text-sm">
              {loading ? 'Saving...' : 'Save'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditUser;
