import React, { useState } from "react";
import ReactDOM from "react-dom";


const Modal = ({ children, onClose, title }) => {
  const [saveButton, setSaveButton] = useState(false);
  return ReactDOM.createPortal(
    <div
      className="popup bg-gray-800 text-white text-right p-4 rounded shadow-md fixed top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 z-50"
      style={{
        maxWidth: "65%", // Genişlik: %50'yi geçmez
        maxHeight: "50%", // Yükseklik: %50'yi geçmez
        overflowY: "auto", // Taşan içerik kaydırılabilir
      }}
    >
      {title && <h3 className="text-lg font-semibold mb-4">{title}</h3>}
      <div>{children}</div>
      <button
        className="mt-4 p-2 bg-red-500 text-white rounded hover:bg-red-600"
        onClick={onClose}
      >
        Close
      </button>
      {saveButton && <button>Save </button>}
    </div>,
    document.body
  );
};

export default Modal;
