import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchWktData, selectWktData, selectHasFetched } from "../store/wktSlice";
import { setId, setLog,  setName, setZoom } from '../store/featureSlice';
import { EditFeature } from "./EditFeature";
import Modal from "./Modal";
import ConfirmModal from "./ConfirmModal";
import { useDeleteItem } from '../utils/deleteFeature';

const FeatureTable = ({ onClose }) => {
  const dispatch = useDispatch();
  const wktData = useSelector(selectWktData);
  const hasFetched = useSelector(selectHasFetched);
  const [showEditTable, setShowEditTable] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [selectedFeature, setSelectedFeature] = useState();
  const deleteItem = useDeleteItem();

  useEffect(() => {
    if (!hasFetched) {
      dispatch(fetchWktData());
    }
  }, [dispatch, hasFetched]);

  const handleViewClick = (log) => {
    dispatch(setZoom(log));
    onClose();
  };

  const handleEditClick = (item) => {
    setSelectedFeature(item); 
    setShowEditTable(true); 
  };

  const handleOpenDeleteModal = (item) => {
    setSelectedFeature(item);
    setShowDeleteModal(true);
    const id= item.id;
    dispatch(setId(id));
  };

  const handleCloseDeleteModal = () => {
    setShowDeleteModal(false);
  };

  const handleDeleteConfirm = () => {
    deleteItem();
    setShowDeleteModal(false);
  }

  return (
    <div>
      <Modal title="Feature List" onClose={onClose}>
      <div>
      {wktData.length > 0 ? (
        <table className="table-auto border-collapse border border-gray-300 w-full">
        <thead>
          <tr>
            <th className="border border-gray-300 px-4 py-2">Name</th>
            <th className="border border-gray-300 px-4 py-2">WKT</th>
            <th className="border border-gray-300 px-4 py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {wktData.map((item) => (
            <tr key={item.id}>
              <td className="border border-gray-300 px-4 py-2">{item.name}</td>
              <td className="border border-gray-300 px-4 py-2 max-w-sm truncate">
              {item.log}
              </td>
              <td className="border border-gray-300 px-4 py-2">
                <button className="bg-blue-500 text-white px-2 py-1 mr-2 rounded hover:bg-blue-700" onClick={() => handleViewClick(item.log)}>
                  View
                </button>
                <button className="bg-yellow-500 text-white px-2 py-1 mr-2 rounded hover:bg-yellow-700" onClick={() => handleEditClick(item)}>
                  Edit
                </button>
                <button className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-700" onClick={() => handleOpenDeleteModal(item)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      ) : (
        <p>Veri bulunamadÄ±.</p>
      )}
    </div>
    {showEditTable && (
        <EditFeature
          id={selectedFeature.id}
          name={selectedFeature.name}
          wkt={selectedFeature.log}
          onClose={() => setShowEditTable(false)}
        />
      )}
    </Modal>
    {showDeleteModal && (
        <ConfirmModal
          title={`Delete '${selectedFeature.name}'`}
          description="Are you sure you would like to do this?"
          onCancel={handleCloseDeleteModal}
          onConfirm={handleDeleteConfirm}
        />
      )}
    </div>
  );
};

export default FeatureTable;
