import React from "react";
import Modal from "./Modal";
import { useDispatch } from "react-redux";
import { setDrawType, setDrawActive } from "../store/drawSlice";

const DrawPopup = ({ onClose }) => {
  const dispatch = useDispatch();

  const handleDrawTypeSelect = (type) => {
    dispatch(setDrawType(type));
    dispatch(setDrawActive(true));
    onClose(); // Popup'u kapat
  };

  return (
    <Modal onClose={onClose} title="Choose a Draw Type">
      <button
        className="block w-full mb-2 p-2 bg-blue-500 text-white rounded hover:bg-green-600"
        onClick={() => handleDrawTypeSelect("Point")}
      >
        Point
      </button>
      <button
        className="block w-full mb-2 p-2 bg-blue-500 text-white rounded hover:bg-green-600"
        onClick={() => handleDrawTypeSelect("LineString")}
      >
        Line
      </button>
      <button
        className="block w-full p-2 bg-blue-500 text-white rounded hover:bg-green-600"
        onClick={() => handleDrawTypeSelect("Polygon")}
      >
        Polygon
      </button>
    </Modal>
  );
};

export default DrawPopup;
