import React, { useState } from "react";
import { WKT } from 'ol/format';
import { setId } from "../store/featureSlice";
import { useDispatch } from "react-redux";
import { useDeleteItem } from '../utils/deleteFeature';
import ConfirmModal from "./ConfirmModal";
import { setFeatureToModify, setShowUpdateModal } from "../store/updateSlice";


const FeaturePopup = ({ feature, onClose }) => {

  const wkt = new WKT().writeFeature(feature);
  const deleteItem = useDeleteItem();
  const id = feature.get('id');
  const dispatch = useDispatch();
  const [showDeleteModal, setShowDeleteModal] = useState(false);

  const handleOpenDeleteModal = () => {
    setShowDeleteModal(true);
    dispatch(setId(id)); 
  }

  const handleUpdate = () => {
    dispatch(setFeatureToModify(feature));
    dispatch(setShowUpdateModal(true));
    onClose();
  }

  const handleCloseDeleteModal = () => {
    setShowDeleteModal(false);
    onClose();
  };

  const handleDeleteConfirm = () => {
    deleteItem();
    setShowDeleteModal(false);
    onClose();
  }
  
  return (
    <div className="fixed z-60 inset-0 overflow-y-auto">
      <div className="flex items-center justify-center min-h-screen px-4 pt-4 pb-20 text-center sm:block sm:p-0">
        <div className="fixed inset-0 transition-opacity">
          <div className="absolute inset-0 bg-gray-500 opacity-75"></div>
        </div>

        <span className="hidden sm:inline-block sm:align-middle sm:h-screen"></span>

        <div
          className="inline-block align-bottom bg-white rounded-lg px-4 pt-5 pb-4 text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full sm:p-6"
        >
          <div className="sm:flex sm:items-start">
            <div className="mx-auto flex-shrink-0 flex items-center justify-center h-12 w-12 rounded-full bg-blue-100 sm:mx-0 sm:h-10 sm:w-10">
              <svg
                className="h-6 w-6 text-blue-600"
                fill="currentColor"
                viewBox="0 0 20 20"
              >
                <path d="M13 7H7v6h6V7z" />
                <path
                  fillRule="evenodd"
                  d="M10 18a8 8 0 100-16 8 8 0 000 16zm4-11a1 1 0 00-1-1H7a1 1 0 00-1 1v6a1 1 0 001 1h6a1 1 0 001-1V7z"
                  clipRule="evenodd"
                />
              </svg>
            </div>

            <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left">
              <h3 className="text-lg leading-6 font-medium text-gray-900">
                {feature?.get("name") || "Feature Name"}
              </h3>
              <div className="mt-2">
                <p className="text-sm text-gray-500">
                  {wkt || "No log available"}
                </p>
              </div>
            </div>
          </div>

          <div className="mt-5 sm:mt-4 sm:flex sm:flex-row-reverse">
            <button
              onClick={handleOpenDeleteModal}
              className="inline-flex justify-center w-full rounded-md border border-transparent px-4 py-2 bg-red-600 text-base font-medium text-white shadow-sm hover:bg-red-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500 sm:ml-3 sm:w-auto sm:text-sm"
            >
              Delete
            </button>
            <button
              onClick={handleUpdate}
              className="mt-3 inline-flex justify-center w-full rounded-md border border-gray-300 px-4 py-2 bg-blue-600 text-base font-medium text-white shadow-sm hover:bg-blue-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 sm:mt-0 sm:w-auto sm:text-sm"
            >
              Update
            </button>
            <button
              onClick={onClose}
              className="mt-3 inline-flex justify-center w-full rounded-md border border-gray-300 px-4 py-2 bg-white text-base font-medium text-gray-700 shadow-sm hover:text-gray-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-300 sm:mt-0 sm:w-auto sm:text-sm"
            >
              Close
            </button>
          </div>
        </div>
      </div>
      {showDeleteModal && (
        <ConfirmModal
          title={`Delete '${feature.name}'`}
          description="Are you sure you would like to do this?"
          onCancel={handleCloseDeleteModal}
          onConfirm={handleDeleteConfirm}
        />
      )}
    </div>
  );
};

export default FeaturePopup;
