import React from "react";
import ReactDOM from "react-dom";

const ReusablePopup = ({ children, onClose, title }) => {
  return ReactDOM.createPortal(
    <div
      className="popup bg-gray-800 text-white text-right p-4 rounded shadow-md fixed top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 z-50"
    >
      {/* Başlık */}
      {title && <h3 className="text-lg font-semibold mb-4">{title}</h3>}
      {/* İçerik */}
      <div>{children}</div>
      {/* Kapatma Butonu */}
      <button
        className="mt-4 p-2 bg-white text-black rounded hover:bg-green-600"
        onClick={onClose}
      >
        Close
      </button>
    </div>,
    document.body
  );
};

export default ReusablePopup;
