import { deleteFeature } from "../store/wktSlice";
import {setDeletedFeatureId} from "../store/featureSlice"
import { useSelector, useDispatch } from 'react-redux';

export const useDeleteItem = () => {
  const featureId = useSelector((state) => state.feature.id);
  const dispatch = useDispatch();

  const deleteItem = async () => {
    try {
      const token = localStorage.getItem('token'); // Token'ı localStorage'dan çekiyoruz

      const response = await fetch(`https://localhost:7014/api/Logs/${featureId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`, // Bearer Token ekleniyor
          'Content-Type': 'application/json'
        }
      });

      const data = await response.json();

      if (!response.ok || !data.result) {
        throw new Error(data.message || 'Failed to delete item');
      }

      dispatch(deleteFeature(featureId));
      dispatch(setDeletedFeatureId(featureId));

      return true;
    } catch (error) {
      console.error(error.message);
      return false;
    }
  };

  return deleteItem;
};
