import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import { useDispatch, useSelector } from "react-redux";
import { fetchWktData, selectWktData, selectHasFetched } from "../store/wktSlice";
import { setId, setLog, setName, setZoom } from '../store/featureSlice';
import { EditFeature } from "./EditFeature";
import ConfirmModal from "./ConfirmModal";
import { useDeleteItem } from '../utils/deleteFeature';

const FeatureTable = ({ onClose }) => {
  const dispatch = useDispatch();
  const wktData = useSelector(selectWktData);
  const hasFetched = useSelector(selectHasFetched);
  const [showEditTable, setShowEditTable] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [selectedFeature, setSelectedFeature] = useState();
  const deleteItem = useDeleteItem();

  useEffect(() => {
    if (!hasFetched) {
      dispatch(fetchWktData());
    }
  }, [dispatch, hasFetched]);

  const handleViewClick = (log) => {
    dispatch(setZoom(log));
    onClose();
  };

  const handleEditClick = (item) => {
    setSelectedFeature(item); 
    setShowEditTable(true); 
  };

  const handleOpenDeleteModal = (item) => {
    setSelectedFeature(item);
    setShowDeleteModal(true);
    const id = item.id;
    dispatch(setId(id));
  };

  const handleCloseDeleteModal = () => {
    setShowDeleteModal(false);
  };

  const handleDeleteConfirm = () => {
    deleteItem();
    setShowDeleteModal(false);
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-gray-500 bg-opacity-75 z-50">
      <div className="relative bg-white rounded-lg overflow-hidden shadow-xl transform transition-all sm:max-w-4xl w-full">
        {/* Sağ üst köşede kapatma butonu */}
        <button
          className="absolute top-3 right-3 text-gray-500 hover:text-gray-700 focus:outline-none"
          onClick={onClose}
        >
          ✕
        </button>

        <div className="p-6">
          <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">Feature List</h3>
          {/* Scrollable tablonun kapsayıcısı */}
          <div className="max-h-96 overflow-y-auto border rounded">
            {wktData.length > 0 ? (
              <table className="table-auto border-collapse w-full text-left">
                <thead className="bg-gray-200">
                  <tr>
                    <th className="px-4 py-2 text-gray-700">Name</th>
                    <th className="px-4 py-2 text-gray-700">WKT</th>
                    <th className="px-4 py-2 text-gray-700">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {wktData.map((item) => (
                    <tr key={item.id} className="border-t">
                      <td className="text-sm leading-5 text-gray-500">{item.name}</td>
                      <td className="text-sm leading-5 text-gray-500">{item.log}</td>
                      <td className="px-4 py-2 flex space-x-2">
                        <button
                          className="inline-flex justify-center w-full rounded-md border border-gray-300 px-4 py-2 bg-white text-base leading-6 font-medium text-gray-700 shadow-sm hover:text-gray-500 focus:outline-none focus:border-blue-300 focus:shadow-outline-blue transition ease-in-out duration-150 sm:text-sm sm:leading-5"
                          onClick={() => handleViewClick(item.log)}
                        >
                          View
                        </button>
                        <button
                          className="inline-flex justify-center w-full rounded-md border border-gray-300 px-4 py-2 bg-white text-base leading-6 font-medium text-gray-700 shadow-sm hover:text-gray-500 focus:outline-none focus:border-blue-300 focus:shadow-outline-blue transition ease-in-out duration-150 sm:text-sm sm:leading-5"
                          onClick={() => handleEditClick(item)}
                        >
                          Edit
                        </button>
                        <button
                          className="inline-flex justify-center w-full rounded-md border border-gray-300 px-4 py-2 bg-white text-base leading-6 font-medium text-gray-700 shadow-sm hover:text-gray-500 focus:outline-none focus:border-blue-300 focus:shadow-outline-blue transition ease-in-out duration-150 sm:text-sm sm:leading-5"
                          onClick={() => handleOpenDeleteModal(item)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p className="text-gray-500 text-center">No data available.</p>
            )}
          </div>
        </div>

        {showDeleteModal && (
          <ConfirmModal
            title="Confirm Delete"
            onConfirm={handleDeleteConfirm}
            onCancel={handleCloseDeleteModal}
          >
            Are you sure you want to delete this feature?
          </ConfirmModal>
        )}

        {showEditTable &&
          ReactDOM.createPortal(
            <EditFeature
              id={selectedFeature.id}
              name={selectedFeature.name}
              wkt={selectedFeature.log}
              onClose={() => setShowEditTable(false)}
            />, document.getElementById("portal-root")
          )}
      </div>
    </div>
  );
};

export default FeatureTable;
